package week3Day1ClassRommStr;

public class EqualsIgnoreCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		String Fruits = "Green Veggies";
		String veggies ="Green Veggies";
		
		
if (Fruits.equalsIgnoreCase(veggies)) {
	System.out.println("Contents are same and ignore case sensitive");
}else {
	System.out.println("Contents are not same and ignore case sensitive");
}	
		
	}

}
