package week3Day1ClassRommStr;

public class learnEquals {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		String fruits = "GreenApple";
		
		int totallength = fruits.length();
		System.out.println("Total lenth of fruits charatcter" + totallength);//lenth of char
		
		//equals
		
		//gonna to use if else condition
		
		if (fruits.equals("GreenApple")){
			System.out.println("contents are same ");
		}else {
			System.out.println("They are not same");
		
		
		}	
	}

}
